class DNodeServer {
  constructor(cons, opts) {
    this.cons = cons;
    this.opts = opts || {};
    this.server = net.createServer();
    this.sessions = {};
    this.weakRefs = new WeakMap();

    if (this.opts.weak) {
      this.server.on('connection', this._handleConnection.bind(this));
    } else {
      this.server.on('connection', this._handleConnection.bind(this));
    }
  }

