class IMAPServer {
  constructor(options) {
    this.options = options || {};
    this.server = null;
    this.connections = new Set();
    this._closeTimeout = null;
    this._setListeners();
  }

  connect(socket, socketOptions) {
    const connection = new IMAPConnection(this, socket, socketOptions);
    this.connections.add(connection);
    connection.on('close', () => this.connections = this.connections.remove(connection));
  }

  listen(...args) {
    const { port, host, secure } = this.options;
    const server = secure ? tls.createServer(this.options.tlsOptions || {}) : net.createServer();
    this.server = server;
    server.on('connection', (socket) => {
      this._handleProxy(socket, (socket) => {
        this.connect(socket, this.options.socketOptions || {});
      });
    });
    server.listen(...args);
  }

  close(callback) {
    if (this.server) {
      this.server.close(() => {
        this.connections.forEach(conn => conn.destroy());
        this.connections = new Set();
        if (callback) callback();
      });
    }
  }

  _setListeners() {
    this.server.on('listening', this._onListening.bind(this));
    this.server.on('close', this._onClose.bind(this));
    this.server.on('error', this._onError.bind(this));
  }

  _onListening() {
    console.log(`IMAP server is listening on ${this.server.address().address}:${this.server.address().port}`);
  }

  _onClose() {
    console.log('IMAP server closed');
  }

  _onError(err) {
    console.error('IMAP server error:', err.message);
  }

  _handleProxy(socket, callback) {
    if (!this.options.proxy) return callback(socket);
    // Proxy protocol handling logic
    let buffer = Buffer.alloc(0);
    socket.on('data', (data) => {
      buffer = Buffer.concat([buffer, data]);
      while (buffer.length >= 128) {
        const len = buffer.readUInt16BE(0);
        if (len > buffer.length - 2) break;
        const header = buffer.slice(0, len + 2);
        const payload = header.slice(2, len + 2);
        if (payload.toString() === 'PROXY') {
          const clientAddr = payload.slice(4).toString().split(':')[0];
          const clientPort = payload.slice(4).toString().split(':')[1];
          const proxyHeader = Buffer.from(`PROXY ${clientAddr} ${clientPort}\r\n`);
          socket.write(proxyHeader);
          buffer = buffer.slice(len + 2);
        } else {
          break;
        }
      }
      if (buffer.length < 128) return;
    });
    socket.on('end', () => {
      callback(socket);
    });
  }

  _upgrade(socket, callback) {
    if (!this.options.secure) return callback(socket);
    const secureSocket = tls.upgradeSecureSocket(socket, this.options.tlsOptions || {});
    callback(secureSocket);
  }
}

class IMAPConnection {
  constructor(server, socket, socketOptions) {
    this.server = server;
    this.socket = socket;
    this.socketOptions = socketOptions;
    this._setup();
  }

  _setup() {
    this.socket.setNoDelay(true);
    this.socket.setTimeout(this.socketOptions.timeout || 120000);
    this.socket.on('timeout', () => {
      this.socket.destroy();
    });
    this.socket.on('end', () => {
      this.emit('close');
    });
  }

  destroy() {
    this.socket.destroy();
  }

  emit(event, ...args) {
    if (event === 'close') {
      this.server.connections = this.server.connections.remove(this);
    }
  }
