    this.origins = origins;
    this.port = options.port || 843;
    this.log = options.log || console.log;
    this.socket = null;
    this.buffer = Buffer.from('');
    this.server = options.server;
  }

  listen(port, server, cb) {
    this.port = port || this.port;
    this.server = server || this.server;

    this.socket = net.createServer((socket) => {
      this.responder(socket);
    });

    this.socket.on('error', (err) => {
      this.log(`Server error: ${err.message}`);
    });

    this.socket.on('listening', () => {
      this.log(`Flash policy server listening on port ${this.port}`);
      if (cb) cb();
    });

    if (this.server) {
      this.socket.attach(this.server);
    } else {
      this.socket.listen(this.port);
    }

    return this;
  }

  responder(socket) {
    socket.write(this.buffer);
    socket.end();
  }

  compile() {
    let xml = '<?xml version="1.0"?>\n<!DOCTYPE cross-domain-policy SYSTEM "http://www.adobe.com/xml/dtds/cross-domain-policy.dtd">\n<cross-domain-policy>\n';
    this.origins.forEach(([domain, port]) => {
      xml += `<allow-access-from domain="${domain}" port="${port}"/>\n`;
    });
    xml += '</cross-domain-policy>';
    this.buffer = Buffer.from(xml);
    return this;
  }

  add(...origins) {
    this.origins.push(...origins);
    return this.compile();
  }

  remove(origin) {
    const index = this.origins.findIndex(([domain, port]) => domain === origin[0] && port === origin[1]);
