const dgram = require('dgram');

function setupUDPServer(argv, io, debug) {
  const udpServer = dgram.createSocket('udp4');
  const streams = {};

  udpServer.on('listening', () => {
    debug(`UDP server listening on ${argv.udpHost || '0.0.0.0'}:${argv.udpPort}`);
  });

  udpServer.on('message', (msg, rinfo) => {
    try {
      const data = JSON.parse(msg.toString());
      const { streamId, payload } = data;

      if (!streams[streamId]) {
        streams[streamId] = [];
      }

      streams[streamId].push(payload);
      if (streams[streamId].length > 100) {
        streams[streamId].shift();
      }

      io.emit('newMessage', { streamId, message: payload });
      io.emit('streamsUpdated', Object.keys(streams));
    } catch (e) {
      debug(`Error parsing UDP message: ${e.message}`);
    }
  });

  udpServer.on('error', (err) => {
    debug(`UDP server error: ${err.message}`);
  });

  udpServer.bind(argv.udpPort, argv.udpHost);
}

