const dgram = require('dgram');
const os = require('os');

class ServerDiscovery {
  constructor() {
    this.nearbyServers = {};
    this.lastDiscoveryBroadcast = 0;
    this.broadcastIP = this.calcBroadcastIP();
    this.discoveryListener = dgram.createSocket('udp4');
  }

  setupDiscovery(callback) {
    this.discoveryListener.on('message', (msg, rinfo) => {
      this.discoveryReceive(msg, rinfo);
    });

    this.discoveryListener.on('error', (err) => {
      console.error(`UDP listener error: ${err.stack}`);
      this.shutdownDiscovery();
    });

    this.discoveryListener.bind(5000, () => {
      console.log('UDP listener bound to port 5000');
      if (callback) callback();
    });
  }

  discoveryBroadcast(type, message, callback) {
    const client = dgram.createSocket('udp4');
    const data = JSON.stringify({ type, ...message });
    client.send(data, 0, data.length, 5000, this.broadcastIP, (err) => {
      if (err) {
        console.error(`Error broadcasting discovery message: ${err.message}`);
      }
      client.close();
      if (callback) callback();
    });
  }

  discoveryReceive(msg, rinfo) {
    try {
      const data = JSON.parse(msg.toString());
      if (data.type === 'heartbeat') {
        const { hostname, lastHeartbeat } = data;
        this.nearbyServers[hostname] = { ...this.nearbyServers[hostname], lastHeartbeat };
        console.log(`Received heartbeat from ${hostname}`);
      }
    } catch (e) {
      console.error(`Error parsing discovery message: ${e.message}`);
    }
  }

  shutdownDiscovery() {
    if (this.discoveryListener) {
      this.discoveryListener.close();
      this.discoveryListener = null;
      console.log('UDP listener closed');
    }
  }

  calcBroadcastIP() {
    const interfaces = os.networkInterfaces();
    for (const interfaceName in interfaces) {
      for (const info of interfaces[interfaceName]) {
        if (!info.internal && info.family === 'IPv4') {
          const [ip, subnetMask] = info.address.split('/');
          const network = ip.substring(0, ip.lastIndexOf('.')) + '.0';
          const broadcast = network.split('.').map((n, i) => {
            if (i === 3) return '255';
            const mask = parseInt(subnetMask.split('/').pop(), 10);
            const netBit = parseInt(n, 10) & ((mask === 32 ? 255 : 0xff << (32 - mask)) >>> 0);
            return netBit.toString(10);
          }).join('.');
          return broadcast;
        }
      }
    }
    return '255.255.255.255';
  }
}

