const dgram = require('dgram');

class UdpPoller {
  constructor(target, timeout, callback) {
    this.udpServer = dgram.createSocket('udp4');
    this.target = target;
    this.timeout = timeout;
    this.callback = callback;
    this.timer = null;
  }

  static get type() {
    return 'udp';
  }

  static validateTarget(target) {
    try {
      const url = new URL(target);
      return url.protocol === 'udp:';
    } catch (e) {
      return false;
    }
  }

  initialize() {
    const url = new URL(this.target);
    this.targetAddress = url.hostname;
    this.targetPort = parseInt(url.port, 10);
    this.udpServer.on('message', this.onResponseCallback.bind(this));
    this.udpServer.on('error', (err) => {
      this.callback(err, null);
    });
  }

  poll() {
    this.udpServer.send('PING', this.targetPort, this.targetAddress, (err) => {
      if (err) {
        this.callback(err, null);
      } else {
        this.timer = setTimeout(() => {
          this.timeoutReached();
        }, this.timeout);
      }
    });
  }

  onResponseCallback(message, sender) {
    if (this.timer) {
      clearTimeout(this.timer);
      this.timer = null;
      this.callback(null, message.toString());
    }
  }

  timeoutReached() {
    if (this.timer) {
      clearTimeout(this.timer);
      this.timer = null;
      this.callback(new Error('Timeout reached'), null);
    }
  }
}

