      this._sock.on('message', (msg, rinfo) => {
        const data = JSON.parse(msg.toString());
        if (data.type === 'worker_connected') {
          this.connect(data.worker);
        } else if (data.type === 'restart') {
          this.masterKilled(data.payload);
        }
