    const message = {
      type: 'call',
      id,
      method,
      args
    };
    this.sock.send(JSON.stringify(message), 0, this.sock.bytesWritten, this.options.address, this.options.port);
  }

  invoke(method, args, worker) {
    if (typeof worker[method] === 'function') {
      worker[method](...args);
    }
  }

  connect(worker) {
    this.children.push(worker);
    this.emit('worker connected', worker);
  }

