      this.emit('connection', socket);
      socket.on('message', (data) => {
        this.emit('message', socket, data);
      });
      socket.on('close', () => {
        this.emit('disconnection', socket);
      });
    });

    this.httpServer.on('error', (err) => {
      this.emit('error', err);
    });

    this.httpServer.on('listening', () => {
      this.state = 1;
      this.emit('started');
    });
  }

  add(socket, data) {
    if (socket && data) {
      socket.send(data);
    }
  }

  close() {
    if (this.state === 1) {
      this.state = 2;
      this.wsServer.close();
      this.httpServer.close();
      this.emit('closed');
    }
  }
}

