        socket.send(JSON.stringify({ 
            id: "Dashboard", 
            type: "registerSubscriber", 
            args: { subscriptionKey: subscriptionKey } 
        }));

        var linkDivs = document.querySelectorAll(".testLinkDiv");
        for (var i = 0, l = linkDivs.length; i < l; i++) {
            var linkDiv = linkDivs[i];
