                var data = message.args.data;
                switch (data.type) {
                    case "singleFailure":
                        li.classList.add("failed");
                        break;
                    case "finished":
                        li.classList.add("reported");
                        li.querySelector(".status").textContent = "- " + (((+data.runtime / 1000) + 1) | 0) + "s";
