function runTests() {
  const socket = new WebSocket('wss://testserver.example.com');
  const maxParallelRuns = 4;
  const subscriptionKey = Math.floor(Math.random() * 1000000);
  const queue = [];
  let numRunning = 0;

  function processQueue() {
    while (numRunning < maxParallelRuns && queue.length > 0) {
      const test = queue.shift();
      openTestWindow(test);
      numRunning++;
    }
  }

  function openTestWindow(test) {
    const win = window.open('', 'test-' + test.id, 'width=800,height=600');
    win.document.write('<html><body><h1>Running Test: ' + test.name + '</h1></body></html>');
    win.document.close();
    socket.send(JSON.stringify({ action: 'start', testId: test.id, subscriptionKey: subscriptionKey }));
  }

  socket.onopen = function () {
    socket.send(JSON.stringify({ action: 'subscribe', subscriptionKey: subscriptionKey }));
    queue.push({ id: 1, name: 'Test 1' });
    queue.push({ id: 2, name: 'Test 2' });
    queue.push({ id: 3, name: 'Test 3' });
    queue.push({ id: 4, name: 'Test 4' });
    queue.push({ id: 5, name: 'Test 5' });
    processQueue();
  };

  socket.onmessage = function (event) {
    const data = JSON.parse(event.data);
    if (data.action === 'result') {
      const resultDiv = document.createElement('div');
      resultDiv.textContent = 'Test ' + data.testId + ' Result: ' + data.status;
      document.body.appendChild(resultDiv);
      numRunning--;
      processQueue();
    }
  };
}

