class RPCWebSocket {
  constructor(address, encryption, namespace) {
    this._namespace = namespace || '';
    this._listeners = {};
    this._handles = {};
    this._socket = null;
    this._reconnectAttempts = 0;
    this._maxReconnectAttempts = 5;
    this._reconnectInterval = 3000;
    this.setAddress(address, encryption);
  }

  setAddress(address, encryption) {
    const protocol = encryption ? 'wss://' : 'ws://';
    this._address = protocol + address;
    this._connect();
  }

  addListener(method, callback) {
    if (!this._listeners[method]) {
      this._listeners[method] = [];
    }
    this._listeners[method].push(callback);
  }

  removeListener(method) {
    delete this._listeners[method];
  }

  request(method, params, id, successCallback, errorCallback) {
    const fullMethod = this._namespace + method;
    const handle = this._generateHandle();
    this._handles[handle] = { success: successCallback, error: errorCallback };
    const data = this._formatData(fullMethod, params, id);
    this._send(data);
    return handle;
  }

  batchRequest(requests, successCallback, errorCallback) {
    const batch = requests.map((req, index) => {
      const fullMethod = this._namespace + req.method;
      const handle = this._generateHandle();
      this._handles[handle] = { success: successCallback, error: errorCallback };
      return this._formatData(fullMethod, req.params, index);
    });
    this._send(batch);
  }

  _formatData(method, params, id) {
    return {
      jsonrpc: '2.0',
      method: method,
      params: params || [],
      id: id !== undefined ? id : this._generateHandle()
    };
  }

  _send(data) {
    if (this._socket && this._socket.readyState === WebSocket.OPEN) {
      this._socket.send(JSON.stringify(data));
    } else {
      console.warn('WebSocket is not open; message not sent.');
    }
  }

  _onerror(error, handles) {
    for (const handle in handles) {
      if (handles[handle].error) {
        handles[handle].error(error);
      }
    }
  }

  _onmessage(message, handles, listeners) {
    try {
      const data = JSON.parse(message.data);
      if (data.id !== undefined) {
        if (handles[data.id]) {
          if (data.error) {
            handles[data.id].error(data.error);
          } else {
            handles[data.id].success(data.result);
          }
          delete handles[data.id];
        }
      } else if (data.method && listeners[data.method]) {
        for (const callback of listeners[data.method]) {
          callback(data.params);
        }
      }
    } catch (e) {
      console.error('Error parsing message:', e);
    }
  }

  _connect() {
    if (this._socket) {
      this._socket.close();
    }
    this._socket = new WebSocket(this._address);
    this._socket.onopen = () => {
      console.log('WebSocket connection established.');
      this._reconnectAttempts = 0;
    };
    this._socket.onmessage = (event) => {
      this._onmessage(event, this._handles, this._listeners);
    };
    this._socket.onerror = (error) => {
      this._onerror(error, this._handles);
      if (this._reconnectAttempts < this._maxReconnectAttempts) {
        this._reconnectAttempts++;
        setTimeout(() => {
          this._connect();
        }, this._reconnectInterval);
      } else {
        console.error('Max reconnection attempts reached.');
      }
    };
    this._socket.onclose = () => {
      console.log('WebSocket connection closed.');
      if (this._reconnectAttempts < this._maxReconnectAttempts) {
        this._reconnectAttempts++;
        setTimeout(() => {
          this._connect();
        }, this._reconnectInterval);
      }
    };
  }

  _generateHandle() {
    return Math.random().toString(36).substr(2, 9);
  }
}

