  const status = document.getElementById("status");
  const output = document.getElementById("output");
  const myField = document.getElementById("myField");

  const websocket = new WebSocket(wsUri);

  websocket.onopen = function (evt) {
    console.log("Connection established");
    status.innerHTML = "Status: Connected";
    websocket.send(myField.value);
    output.innerHTML = "Sent: " + myField.value + "<br>" + output.innerHTML;
  };

  websocket.onclose = function (evt) {
    console.log("Connection closed");
    status.innerHTML = "Status: Disconnected";
  };

  websocket.onmessage = function (evt) {
    console.log("Received: " + evt.data);
    output.innerHTML = "Received: " + evt.data + "<br>" + output.innerHTML;
  };

  websocket.onerror = function (evt) {
    console.log("Error occurred: " + evt.data);
    status.innerHTML = "Status: Error";
  };
}

