    this.emit = this.emitter.emit.bind(this.emitter);
  }

  open() {
    if (this.state === 'CONNECTING' || this.state === 'CONNECTED') {
      return;
    }
    this.state = 'CONNECTING';
    this.connectPromise = new Promise((resolve, reject) => {
      try {
        const socket = new WebSocket(this.serverURL);
        this.socket = socket;
        socket.onopen = () => this._handleWebSocketOpen(resolve, reject);
        socket.onmessage = (event) => this._handleWebSocketMessage(event);
        socket.onclose = () => this._handleWebSocketClose();
        socket.onerror = (error) => this._handleWebSocketError(error);
      } catch (error) {
        this._handleWebSocketError(error);
        reject(error);
      }
    });
  }

  _handleWebSocketOpen(resolve, reject) {
    this.state = 'CONNECTED';
    this._sendConnectRequest().then(() => {
      resolve();
    }).catch((error) => {
      reject(error);
    });
  }

  _sendConnectRequest() {
    return new Promise((resolve, reject) => {
      const request = {
        op: 'connect',
        applicationId: this.applicationId,
        javascriptKey: this.javascriptKey,
        masterKey: this.masterKey,
        sessionToken: this.sessionToken,
        installationId: this.installationId,
        additionalProperties: this.additionalProperties
      };
      this.socket.send(JSON.stringify(request));
      this.socket.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.op === 'connected') {
          this.id = data.clientId;
          this.emit('open');
          resolve();
        }
      };
    });
  }

  _handleWebSocketMessage(event) {
    const data = JSON.parse(event.data);
    if (data.op === 'connected') {
      this.id = data.clientId;
      this.emit('open');
    } else if (data.op === 'subscribe') {
      const subscription = this.subscriptions.get(data.subscriptionId);
      if (subscription) {
        subscription._handleMessage(data);
      }
    } else if (data.op === 'unsubscribe') {
      const subscription = this.subscriptions.get(data.subscriptionId);
      if (subscription) {
        subscription._handleUnsubscribe(data);
      }
    } else if (data.op === 'update') {
      const subscription = this.subscriptions.get(data.subscriptionId);
      if (subscription) {
        subscription._handleUpdate(data);
      }
    } else if (data.op === 'error') {
      this.emit('error', data.error);
    }
  }

  _handleWebSocketClose() {
    this.state = 'CLOSED';
    this.emit('close');
    this._handleReconnect();
  }

  _handleWebSocketError(error) {
    this.emit('error', error);
    this._handleReconnect();
  }

  _handleReconnect() {
    if (this.state === 'CLOSED') {
      this.attempts++;
      const delay = Math.min(1000 * Math.pow(2, this.attempts), 30000);
      this.reconnectHandle = setTimeout(() => {
        this.open();
      }, delay);
    }
  }
}

