class WebSocketRPC {
  constructor() {
    this._ = null;
    this.JSON = JSON;
    this.utils = {
      uuid: () => {
        return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
          const r = Math.random() * 16 | 0, v = c === 'x' ? r : (r & 0x3 | 0x8);
          return v.toString(16);
        });
      }
    };
    this.alerts = {
      error: (msg) => {
        console.error(msg);
      }
    };
    this.initialized = false;
    this.handles = [];
    this.sock = null;
    this.conf = null;
    this.scheme = 'ws';
    this.onready = null;
  }

  onerror(ev) {
    this.alerts.error('WebSocket error:', ev);
    this.handles.forEach((handle) => {
      if (handle.err) handle.err(new Error('WebSocket connection error'));
    });
    this.handles = [];
    this.initialized = false;
  }

  onclose(ev) {
    this.alerts.error('WebSocket closed:', ev);
    this.handles.forEach((handle) => {
      if (handle.err) handle.err(new Error('WebSocket connection closed'));
    });
    this.handles = [];
    this.initialized = false;
  }

  onopen() {
    this.initialized = true;
    if (this.onready) this.onready();
  }

  onmessage(message) {
    try {
      const data = this.JSON.parse(message.data);
      if (data.id && this.handles.length > 0) {
        const handle = this.handles.find(h => h.id === data.id);
        if (handle) {
          if (data.error) {
            handle.err(data.error);
          } else {
            handle.success(data.result);
          }
          this.handles = this.handles.filter(h => h.id !== data.id);
        }
      }
    } catch (e) {
      this.alerts.error('Error parsing message:', e);
    }
