      jsonrpc: '2.0',
      method: opts.name,
      params: opts.params || [],
      id: id
    };

    const handle = {
      id: id,
      success: opts.success,
      err: opts.error
    };

    this.handles.push(handle);
    this.sock.send(this.JSON.stringify(request));
  }

  init(conf, onready) {
    this.conf = conf;
    this.onready = onready;
    this.scheme = conf.encrypt ? 'wss' : 'ws';
