function startRecording(data) {
  const { host, port, language, task, modelSize, useVad } = data;
  const wsUrl = `ws://${host}:${port}`;
  let ws = null;
  let mediaRecorder = null;
  let audioChunks = [];
  let isServerReady = false;
  let isCapturing = false;

  const initAudioStream = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      mediaRecorder = new MediaRecorder(stream);
      mediaRecorder.ondataavailable = (event) => {
        if (event.data.size > 0) {
          audioChunks.push(event.data);
        }
      };
      mediaRecorder.onstop = () => {
        const audioBlob = new Blob(audioChunks, { type: 'audio/webm' });
        audioChunks = [];
        if (ws && ws.readyState === WebSocket.OPEN) {
          ws.send(audioBlob);
        }
      };
    } catch (err) {
      console.error('Microphone access denied or failed:', err);
      browser.runtime.sendMessage({ type: 'error', message: 'Microphone access denied or failed' });
    }
  };

  const connectWebSocket = () => {
    ws = new WebSocket(wsUrl);
    ws.binaryType = 'arraybuffer';

    ws.onopen = () => {
      console.log('WebSocket connection established');
      browser.runtime.sendMessage({ type: 'status', message: 'Connected to server' });
      sendInitializationMessage();
    };

    ws.onmessage = (event) => {
      if (event.data instanceof ArrayBuffer) {
        const arrayBuffer = event.data;
        const uint8Array = new Uint8Array(arrayBuffer);
        const blob = new Blob([uint8Array], { type: 'audio/webm' });
        browser.runtime.sendMessage({ type: 'audio', data: blob });
      } else {
        const message = JSON.parse(event.data);
        if (message.type === 'status') {
          browser.runtime.sendMessage({ type: 'status', message: message.detail });
        } else if (message.type === 'transcription') {
          browser.runtime.sendMessage({ type: 'transcription', text: message.text });
        }
      }
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      browser.runtime.sendMessage({ type: 'error', message: 'WebSocket error occurred' });
    };

    ws.onclose = () => {
      console.log('WebSocket connection closed');
      browser.runtime.sendMessage({ type: 'status', message: 'Connection closed' });
      if (isCapturing) {
        mediaRecorder.stop();
        isCapturing = false;
      }
    };
  };

  const sendInitializationMessage = () => {
    const initMessage = {
      type: 'init',
      language,
      task,
      modelSize,
      useVad
    };
    ws.send(JSON.stringify(initMessage));
  };

  const startCapture = () => {
    if (!mediaRecorder) {
      browser.runtime.sendMessage({ type: 'error', message: 'Audio capture not initialized' });
      return;
    }
    if (mediaRecorder.state === 'recording') {
      mediaRecorder.stop();
      isCapturing = false;
    } else {
      mediaRecorder.start(1000); // Capture audio in 1s chunks
      isCapturing = true;
    }
  };

  const stopCapture = () => {
    if (mediaRecorder && mediaRecorder.state === 'recording') {
      mediaRecorder.stop();
      isCapturing = false;
    }
  };

  const reconnectWebSocket = () => {
    if (ws && ws.readyState === WebSocket.CLOSING || ws.readyState === WebSocket.CLOSED) {
