class BingChatWebSocket {
  private webSockets: Record<string, WebSocket | null> = {};

  private generateSocketId(): string {
    return 'socket-' + Math.random().toString(36).substr(2, 9);
  }

  private async establishConnection(socketId: string): Promise<void> {
    const ws = new WebSocket('wss://chat.example.com');

    return new Promise((resolve, reject) => {
      ws.onopen = () => {
        this.webSockets[socketId] = ws;
        resolve();
      };

      ws.onerror = (error) => {
        reject(error);
      };
    });
  }

  public async getSocketId(): Promise<string> {
    const socketId = this.generateSocketId();
    try {
      await this.establishConnection(socketId);
      return socketId;
    } catch (error) {
      console.error('Failed to establish WebSocket connection:', error);
      throw error;
    }
  }

  public async ping(socketId: string): Promise<void> {
    const ws = this.webSockets[socketId];
    if (!ws || ws.readyState !== WebSocket.OPEN) {
      throw new Error(`WebSocket for socketId ${socketId} is not open.`);
    }

    return new Promise((resolve, reject) => {
      ws.send(JSON.stringify({ type: 'ping' }));
      ws.addEventListener('message', (event) => {
        try {
          const data = JSON.parse(event.data);
          if (data.type === 'pong') {
            resolve();
          }
        } catch (e) {
          reject(e);
        }
      }, { once: true });
    });
  }

  public async send(
    socketId: string,
    msg: object,
    onMessage: (data: any) => void
  ): Promise<any> {
    const ws = this.webSockets[socketId];
    if (!ws || ws.readyState !== WebSocket.OPEN) {
      throw new Error(`WebSocket for socketId ${socketId} is not open.`);
    }

    return new Promise((resolve, reject) => {
      let receivedResponse = false;

      ws.addEventListener('message', (event) => {
        try {
          const data = JSON.parse(event.data);
          if (data.type === 2) {
            receivedResponse = true;
            resolve(data);
          }
          onMessage(data);
        } catch (e) {
          reject(e);
        }
      });

      try {
        ws.send(JSON.stringify(msg));
      } catch (e) {
        reject(e);
      }

      const timeout = setTimeout(() => {
        if (!receivedResponse) {
          reject(new Error('No response received within the timeout.'));
        }
      }, 10000);

      // Clean up event listener and timeout after resolution or rejection
      return () => {
        clearTimeout(timeout);
        ws.removeEventListener('message', () => {});
      };
    });
  }

  public async close(socketId: string): Promise<void> {
    const ws = this.webSockets[socketId];
    if (!ws) {
      return;
    }

    return new Promise((resolve, reject) => {
      ws.close();
      ws.addEventListener('close', () => {
        delete this.webSockets[socketId];
        resolve();
      });

      ws.addEventListener('error', (error) => {
        reject(error);
      });
    });
  }
}

