        this.appendConsole(data.payload);
      } else if (data.type === 'execution_complete') {
        this.running = false;
        this.appendConsole('Execution completed.');
      } else if (data.type === 'debug_step') {
        this.setLineGoThrough(data.payload.line);
      }
    };
    this.ws.onclose = () => {
      this.wsOpen = false;
      console.log('WebSocket connection closed.');
    };
    this.ws.onerror = (error) => {
      console.error('WebSocket error:', error);
    };
  }

  async runPython(code) {
    return new Promise((resolve, reject) => {
      if (!this.wsOpen) {
        reject(new Error('WebSocket is not open.'));
        return;
      }
      if (this.running) {
        reject(new Error('Python code is already running.'));
        return;
      }
      this.running = true;
      this.resetConsole();
      this.ws.send(JSON.stringify({ type: 'run_code', payload: code }));
      this.ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === 'execution_complete') {
          this.running = false;
          resolve(data.payload);
        } else if (data.type === 'error') {
          this.running = false;
          reject(new Error(data.payload));
        }
      };
    });
  }

