class CocogoatWebControl {
  port: number;
  token: string;
  hwnd: number;
  version: string;
  ev: any;
  client: any;
  ws: WebSocket | undefined;

  constructor(_port?: number) {
    this.port = _port || 32333;
    this.token = "";
    this.hwnd = 0;
    this.version = "1.0.0";
    this.ev = new Mitt();
    this.client = new FlyType();
    this.ws = undefined;
  }

  async authorize(): Promise<boolean> {
    return new Promise((resolve) => {
      const ws = new WebSocket(`ws://localhost:${this.port}`);
      this.ws = ws;

      ws.onopen = () => {
        console.log("WebSocket connection established.");
        this.sendAuthRequest();
      };

      ws.onmessage = (event) => {
        const data = JSON.parse(event.data);
        if (data.type === "auth") {
          this.token = data.token;
          resolve(true);
        } else {
          resolve(false);
        }
      };

      ws.onerror = (error) => {
        console.error("WebSocket error:", error);
        resolve(false);
      };

      ws.onclose = () => {
        console.log("WebSocket connection closed.");
        resolve(false);
      };
    });
  }

  sendAuthRequest() {
    if (this.ws && this.ws.readyState === WebSocket.OPEN) {
      this.ws.send(JSON.stringify({ type: "auth", hwnd: this.hwnd }));
    }
  }

  async wsInvoke(method: string, path: string, querystring?: Record<string, any>): Promise<{ status: number; body: any }> {
    return new Promise((resolve, reject) => {
      if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
        reject(new Error("WebSocket is not open."));
        return;
