        method,
        path,
        querystring,
        token: this.token,
      };

      this.ws.send(JSON.stringify(payload));

      this.ev.on("response", (response: { status: number; body: any }) => {
        resolve(response);
      });

      this.ws.onerror = (error) => {
        reject(error);
      };

      this.ws.onclose = () => {
        reject(new Error("WebSocket connection closed during invocation."));
      };
    });
  }
}

