async function getSubscribedChannels(lang = 'id') {
  try {
    const configPage = await axios.get('https://streaming-service.com/config');
    const $ = cheerio.load(configPage.data);

    const authUrl = $('meta[name="auth-url"]').attr('content');
    const clientId = $('meta[name="client-id"]').attr('content');
    const clientSecret = $('meta[name="client-secret"]').attr('content');

    const authResponse = await axios.post(authUrl, {
      client_id: clientId,
      client_secret: clientSecret,
      grant_type: 'client_credentials'
    });

    const accessToken = authResponse.data.access_token;

    const packagesResponse = await axios.get('https://streaming-service.com/api/packages', {
      headers: { Authorization: `Bearer ${accessToken}` }
    });

    const packageIds = packagesResponse.data.packages.map(pkg => pkg.id);

    const channels = [];
    for (const id of packageIds) {
      const channelResponse = await axios.get(`https://streaming-service.com/api/channels?package_id=${id}`, {
        headers: { Authorization: `Bearer ${accessToken}` }
      });

      const uniqueChannels = {};
      channelResponse.data.channels.forEach(channel => {
        if (!uniqueChannels[channel.site_id]) {
          uniqueChannels[channel.site_id] = {
            lang,
            site_id: channel.site_id,
            name: channel.name
          };
        }
      });

      Object.values(uniqueChannels).forEach(ch => channels.push(ch));
    }

    return channels;
  } catch (error) {
    throw new Error(`Failed to fetch or process channel data: ${error.message}`);
  }
}

