const axios = require('axios');
const fs = require('fs');
const path = require('path');

function downloadIcons(url, style, callback) {
  const targetImagePath = './icons/';
  const duplicateFile = path.join(targetImagePath, 'duplicates.txt');

  if (!fs.existsSync(targetImagePath)) {
    fs.mkdirSync(targetImagePath, { recursive: true });
  }

  axios.get(url, {
    headers: {
      'Accept': 'application/json',
      'Content-Type': 'application/json',
      'withCredentials': true
    }
  })
  .then(response => {
    const iconsData = response.data;
    const processedIcons = [];
    const seenNames = new Set();

    iconsData.forEach(icon => {
      const { name, svg, category, tags, code, unicode, pro } = icon;

      if (seenNames.has(name)) {
        fs.appendFileSync(duplicateFile, `${name}\n`);
        return;
      }

      seenNames.add(name);

      const iconPath = path.join(targetImagePath, `${name}.svg`);
      fs.writeFileSync(iconPath, svg);

      processedIcons.push({
        name,
        svg,
        category,
        style,
        tags,
        code,
        unicode,
        pro
      });
    });

    processedIcons.sort((a, b) => a.name.localeCompare(b.name));
    callback(null, processedIcons);
  })
  .catch(error => {
    console.error('Error downloading or processing icons:', error.message);
    callback(error, null);
  });
}

