function getNote(url, instrument, name, audioContext) {
  const cacheKey = `${instrument}-${name}`;
  const cached = window.__audioCache || (window.__audioCache = {});
  if (cached[cacheKey]) {
    return Promise.resolve({
      instrument,
      name,
      status: 'loaded',
      audioBuffer: cached[cacheKey]
    });
  }

  return new Promise((resolve, reject) => {
    const request = new XMLHttpRequest();
    const path = `${url}/${instrument}/${name}.wav`;
    request.open('GET', path, true);
    request.responseType = 'arraybuffer';

    request.onload = () => {
      if (request.status === 200) {
        audioContext.decodeAudioData(request.response, buffer => {
          if (buffer) {
            cached[cacheKey] = buffer;
            resolve({
              instrument,
              name,
              status: 'loaded',
              audioBuffer: buffer
            });
          } else {
            reject(new Error('Failed to decode audio data'));
          }
        }, error => {
          reject(new Error(`Audio decoding error: ${error}`));
        });
      } else {
        reject(new Error(`Failed to load audio file: ${path}`));
      }
    };

    request.onerror = () => {
      reject(new Error(`Network error while loading audio file: ${path}`));
    };

    request.send();
  });
}

