function createServer() {
  const express = require('express');
  const app = express();
  const http = require('http').createServer(app);
  const port = process.env.PORT || 3000;
  const randomPort = Math.floor(Math.random() * (65535 - 1024 + 1)) + 1024;

  app.use(express.json());
  app.use(express.urlencoded({ extended: true }));
  app.use(express.static('public'));

  app.get('/update', (req, res) => {
    // Trigger reindexing logic
    res.status(200).send('Reindexing triggered');
  });

  app.get('/embed', (req, res) => {
    // Handle oEmbed requests
    const { url } = req.query;
    if (!url) return res.status(400).send('URL parameter is required');

    try {
      const result = oembed.get(url);
      res.json(result);
    } catch (err) {
      res.status(500).send('Error fetching oEmbed data');
    }
  });

  app.post('/events', (req, res) => {
    // Process queued events
    const events = req.body.events || [];
    if (!Array.isArray(events)) return res.status(400).send('Invalid event format');

    async.eachSeries(events, (event, callback) => {
      try {
        // Process each event
        logger.info(`Processing event: ${JSON.stringify(event)}`);
        callback();
      } catch (err) {
        logger.error(`Error processing event: ${err.message}`);
        callback(err);
      }
    }, (err) => {
      if (err) return res.status(500).send('Error processing events');
      res.status(200).send('Events processed successfully');
    });
  });

  // Dynamically generate API routes based on utility functions
  const util = require('util');
  const functions = Object.keys(dataIn).filter(key => typeof dataIn[key] === 'function');

  functions.forEach(funcName => {
    app.get(`/${funcName}`, (req, res) => {
      try {
        const result = dataIn[funcName](req.query);
        res.json(result);
      } catch (err) {
        res.status(500).send(`Error in ${funcName}: ${err.message}`);
      }
    });
  });

  http.listen(randomPort, () => {
    console.log(JSON.stringify({ port: randomPort }));
  });
}

