          }
          res.json(attraction);
        })
        .catch(err => {
          console.error(err);
          res.status(500).json({ error: 'Failed to retrieve attraction details' });
        });
    });

    const domain = require('domain').create();
    domain.on('error', err => {
      console.error('Domain error:', err);
      server.close(() => process.exit(1));
