function getServer(P) {
  const app = express();
  const port = P || process.env.PORT || 8000;
  const isReferenceMode = port === 8000;

  // Set process title based on mode
  process.title = isReferenceMode ? 'regression-test-reference' : 'regression-test-dev';

  // Setup Handlebars
  app.engine('handlebars', exphbs({
    defaultLayout: 'main',
    layoutsDir: isReferenceMode ? path.join(__dirname, 'views/reference') : path.join(__dirname, 'views/dev'),
    partialsDir: isReferenceMode ? path.join(__dirname, 'views/reference/partials') : path.join(__dirname, 'views/dev/partials')
  }));
  app.set('view engine', 'handlebars');

  // Static file serving
  app.use(express.static(isReferenceMode ? path.join(__dirname, 'public/reference') : path.join(__dirname, 'public/dev')));

  // Route for component testing
  app.get('/test/:component', (req, res) => {
    const component = req.params.component;
    res.render('test', { component });
  });

  // Start server
  const server = app.listen(port, () => {
    console.log(`Server running in ${isReferenceMode ? 'reference' : 'development'} mode on port ${port}`);
  });

  return server;
}

