const express = require('express');
const fs = require('fs');
const path = require('path');
const responseTime = require('response-time');

function setupExpressServer() {
  const app = express();
  const PORT = 3000;
  const imagePath = path.join(__dirname, 'CapitalOne_Digital_Skills_Infographic_BW.jpg');

  app.use(responseTime());

  app.get('/non-streaming-sync', (req, res) => {
    try {
      const data = fs.readFileSync(imagePath);
      res.send(data);
    } catch (err) {
      res.status(500).send('Error reading file');
    }
  });

  app.get('/non-streaming-async', (req, res) => {
    fs.readFile(imagePath, (err, data) => {
      if (err) return res.status(500).send('Error reading file');
      res.send(data);
    });
  });

  app.get('/streaming-pipe', (req, res) => {
    fs.createReadStream(imagePath).pipe(res);
  });

  app.get('/streaming-manual', (req, res) => {
    const stream = fs.createReadStream(imagePath);
    stream.on('data', (chunk) => res.write(chunk));
    stream.on('end', () => res.end());
    stream.on('error', (err) => res.status(500).send('Stream error'));
  });

  app.listen(PORT, () => {
    console.log(`Server running on port ${PORT}`);
  });
}

