function setupExpressServer() {
  const app = express();
  app.use(cors({ origin: '*', methods: 'GET' }));

  app.get('/wikipedia_summaries', async (req, res) => {
    const keyword = req.query.keyword;
    if (!keyword) {
      return res.status(400).json({ error: 'Keyword parameter is required' });
    }

    try {
      const links = await getWikipediaLinks(keyword);
      const results = await Promise.all(links.map(link => 
        fetchSummaryAndCategories(link)
      ));
      res.json(results);
    } catch (error) {
      res.status(500).json({ error: 'Failed to fetch Wikipedia data' });
    }
  });

  const PORT = process.env.PORT || 3000;
  app.listen(PORT, () => {
    console.log(`Server is running on port ${PORT}`);
  });
}

