const http = require('http');
const fs = require('fs');

function createServer() {
    let connectionCounter = 0;
    const activeConnections = new Map();

    const server = http.createServer((req, res) => {
        const socket = req.socket;
        const connInfo = activeConnections.get(socket);
        if (connInfo) {
            req.pipe(connInfo.stream, { end: false });
        }

        if (req.url === '/' && req.method === 'GET') {
            res.writeHead(200, { 'Content-Type': 'text/html' });
            res.end('<form method="POST" enctype="multipart/form-data"><input type="file" name="file"><button type="submit">Upload</button></form>');
        } else {
            res.writeHead(200, { 'Content-Type': 'text/plain' });
            res.end(`Active connections: ${connectionCounter}\nTotal connections so far: ${connInfo ? connInfo.id : 'unknown'}`);
        }
    });

    server.on('connection', (socket) => {
        connectionCounter++;
        const connId = connectionCounter;
        const filename = `connection_${connId}.txt`;
        const fileStream = fs.createWriteStream(filename);

        activeConnections.set(socket, { id: connId, stream: fileStream });

        console.log(`Connection established: ${connId}`);

        socket.on('close', () => {
            connectionCounter--;
            const { stream } = activeConnections.get(socket) || {};
            if (stream) {
                stream.end();
            }
            activeConnections.delete(socket);
            console.log(`Connection closed: ${connId}`);
        });

        socket.on('error', (err) => {
            console.error(`Socket error on ${connId}:`, err);
        });
    });

    server.on('error', (err) => {
        console.error('Server error:', err);
    });

    const port = process.env.PORT || 3000;
    server.listen(port, () => {
        console.log(`Server listening on port ${port}`);
    });
}

