    const filename = `request_${timestamp}_${id}.log`;
    const writeStream = fs.createWriteStream(path.join(__dirname, filename));

    writeStream.write(`Timestamp: ${new Date().toISOString()}\n`);
    writeStream.write(`Method: ${req.method}\n`);
    writeStream.write(`URL: ${req.url}\n`);
    writeStream.write(`HTTP Version: ${req.httpVersion}\n`);
    writeStream.write('Headers:\n');
    writeStream.write(JSON.stringify(req.headers, null, 2) + '\n\n');

    req.pipe(writeStream, { end: false });

    req.on('end', () => {
      writeStream.end();
    });

    if (req.method === 'GET' && req.url === '/upload') {
      res.writeHead(200, { 'Content-Type': 'text/html' });
