    const fetchChannels = async (provider) => {
        try {
            const response = await axios.post('https://api.tv.guide/v1/channels', {
                provider_id: provider.id,
                date: today
            });

            const $ = cheerio.load(response.data);
            return $('.channel-card').map((i, el) => {
                const $el = $(el);
                return {
                    name: $el.find('.channel-name').text().trim(),
                    site_id: `${$el.data('channel-number')}-${$el.data('channel-slug')}`
                };
            }).get();
        } catch (error) {
            console.error(`Failed to fetch ${provider.name}:`, error.message);
            return [];
        }
    };

    const channelPromises = providers.map(fetchChannels);
    const channelArrays = await Promise.all(channelPromises);
    const allChannels = channelArrays.flat();
    const uniqueChannels = _.uniqBy(allChannels, 'site_id');

    return uniqueChannels.map(channel => ({
        lang: 'it',
        name: channel.name,
        site_id: channel.site_id
    }));
}

