const http = require('http');
const fs = require('fs');
const path = require('path');
const send = require('send');
const marked = require('marked');

class Server {
  constructor(config) {
    this.config = { ...{ port: 8000, path: 'public' }, ...(config || {}) };
    this._http = http.createServer();
    this.sockets = [];
    this.path = this.config.path;
    this.updateServer();
  }

  updateServer() {
    this._http.on('request', (req, res) => {
      let filePath = path.resolve(this.path, req.url.split('/').map(decodeURIComponent).join('/'));

      if (!filePath.startsWith(path.resolve(this.path))) {
        res.statusCode = 403;
        res.end('Forbidden');
        return;
      }

      fs.stat(filePath, (err, stats) => {
        if (err || !stats) {
          res.statusCode = 404;
          res.end('Not Found');
          return;
        }

        if (stats.isDirectory()) {
          if (!req.url.endsWith('/')) {
            res.statusCode = 301;
            res.setHeader('Location', req.url + '/');
            res.end();
            return;
          }

          const indexPath = path.join(filePath, 'index.html');
          fs.stat(indexPath, (err, indexStats) => {
            if (!err && indexStats.isFile()) {
              send(req, indexPath).pipe(res);
              return;
            }

            const readmePath = path.join(filePath, 'README.md');
            fs.stat(readmePath, (err, readmeStats) => {
              if (!err && readmeStats.isFile()) {
                fs.readFile(readmePath, 'utf8', (err, data) => {
                  if (err) {
                    res.statusCode = 500;
                    res.end('Internal Server Error');
                    return;
                  }
                  marked.parse(data, (err, html) => {
                    if (err) {
                      res.statusCode = 500;
                      res.end('Internal Server Error');
                      return;
                    }
                    res.setHeader('Content-Type', 'text/html');
                    res.end(html);
                  });
                });
                return;
              }

              this.generateDirectoryListing(filePath, req.url, res);
            });
          });
        } else {
          send(req, filePath).pipe(res);
        }
      });
    });

    this._http.on('connection', (socket) => {
      this.sockets.push(socket);
      socket.on('close', () => {
        const index = this.sockets.indexOf(socket);
        if (index !== -1) {
          this.sockets.splice(index, 1);
        }
      });
    });
  }

  generateDirectoryListing(filePath, url, res) {
    fs.readdir(filePath, (err, files) => {
      if (err) {
        res.statusCode = 500;
        res.end('Unable to read directory');
        return;
      }

      const parentDir = url === '/' ? '' : path.dirname(url.slice(0, -1));
      const html = `
        <html>
        <head><title>Directory: ${url}</title></head>
        <body>
          <h1>Directory Listing: ${url}</h1>
          <ul>
            ${parentDir ? `<li><a href="${parentDir}/">..</a></li>` : ''}
            ${files.map(file => {
              const encodedFile = encodeURIComponent(file);
              return `<li><a href="${url}${encodedFile}">${file}</a></li>`;
            }).join('')}
          </ul>
        </body>
        </html>
      `;

      res.setHeader('Content-Type', 'text/html');
      res.end(html);
    });
  }

  start(callback) {
    this._http.listen(this.config.port, callback);
  }

  stop(callback) {
    this._http.close(() => {
      this.sockets.forEach(socket => {
        try {
          socket.destroy();
        } catch (e) {}
      });
      if (callback) callback();
    });
  }
}

module.exports = Server;

