function createServer() {
  const port = process.argv[2] || 90;
  const roots = ['../src', '../dist'];
  const cache = {};
  const binaryExtensions = ['.png', '.jpg', '.jpeg', '.gif', '.pdf', '.mp4', '.avi', '.ico', '.woff', '.ttf', '.eot'];
  const contentTypes = {
    '.html': 'text/html',
    '.css': 'text/css',
    '.js': 'application/javascript',
    '.json': 'application/json',
    '.txt': 'text/plain',
    '.png': 'image/png',
    '.jpg': 'image/jpeg',
    '.jpeg': 'image/jpeg',
    '.gif': 'image/gif',
    '.pdf': 'application/pdf',
    '.mp4': 'video/mp4',
    '.avi': 'video/x-msvideo',
    '.ico': 'image/x-icon',
    '.woff': 'font/woff',
    '.woff2': 'font/woff2',
    '.ttf': 'font/ttf',
    '.eot': 'application/vnd.ms-fontobject'
  };

  roots.forEach(root => {
    const absoluteRoot = path.resolve(__dirname, root);
    function walk(currentPath) {
      fs.readdirSync(currentPath).forEach(file => {
        const fullPath = path.join(currentPath, file);
        const stat = fs.statSync(fullPath);
        if (stat.isDirectory()) {
          walk(fullPath);
        } else {
          const relPathInRoot = path.relative(absoluteRoot, fullPath);
          const cacheKey = '/' + relPathInRoot.split(path.sep).join('/');
          const ext = path.extname(file).toLowerCase();
          const isBinary = binaryExtensions.includes(ext);
          const contentType = contentTypes[ext] || 'application/octet-stream';
          if (isBinary) {
            cache[cacheKey] = {
              type: 'binary',
              path: fullPath,
              contentType
            };
          } else {
            try {
              const content = fs.readFileSync(fullPath, 'utf8');
              cache[cacheKey] = {
                type: 'text',
                content,
                contentType
              };
            } catch (e) {}
          }
        }
      });
    }
    walk(absoluteRoot);
  });

  console.log('Loaded files:');
  Object.keys(cache).forEach(file => console.log(file));

  const server = http.createServer((req, res) => {
    const parsedUrl = url.parse(req.url, true);
    let pathname = parsedUrl.pathname;

    if (pathname === '/') {
      pathname = '/index.html';
    }

    const filesToCombine = parsedUrl.query.files;
    if (pathname.endsWith('.js') && filesToCombine) {
      const filePaths = filesToCombine.split(',').map(f => f.trim());
      let combinedContent = '';
      for (const fp of filePaths) {
        const cached = cache[fp];
        if (!cached || cached.type !== 'text' || !cached.contentType.startsWith('application/javascript')) {
          res.writeHead(404, { 'Content-Type': 'text/plain' });
          res.end(`File not found or not a JS file: ${fp}`);
          return;
        }
        combinedContent += cached.content + '\n';
      }
      res.writeHead(200, { 'Content-Type': 'application/javascript' });
      res.end(combinedContent);
      return;
    }

    const cached = cache[pathname];
    if (cached) {
      if (cached.type === 'text') {
        res.writeHead(200, { 'Content-Type': cached.contentType });
        res.end(cached.content);
      } else if (cached.type === 'binary') {
        res.writeHead(200, { 'Content-Type': cached.contentType });
        fs.createReadStream(cached.path).pipe(res);
      }
    } else {
      res.writeHead(404, { 'Content-Type': 'text/plain' });
      res.end('404 Not Found');
    }
  });

  server.listen(port, '127.0.0.1', () => {
    console.log(`Server running at http://127.0.0.1:${port}/`);
  });
}

