  const buffer = receive.data;
  let length = 0;
  let data;
  let code;
  if(buffer.length < 2) { return; }
  length = buffer[0] * 256 + buffer[1];
  if(buffer.length >= length + 2) {
    data = buffer.slice(2, length - 2);
    code = buffer.slice(length - 2);
