  const dataBuffer = Buffer.from(message);
  const length = dataBuffer.length;
  const lengthBuffer = Buffer.from(('0000000000000000' + length.toString(16)).substr(-8), 'hex');
  const pack = Buffer.concat([lengthBuffer, dataBuffer]);
  return pack;
}

