function checkData(receive, password, commandHandler) {
    if (receive.data.length < 4) return;
    const length = receive.data.readUInt32BE(0);
    if (receive.data.length < 4 + length) return;
    const packet = receive.data.slice(4, 4 + length);
    receive.data = receive.data.slice(4 + length);
    if (packet.length < 16) return;
    const receivedCode = packet.slice(0, 16);
    const dataBuf = packet.slice(16);
    if (checkCode(dataBuf, password, receivedCode)) {
        commandHandler(dataBuf).then(response => {
            const packed = pack(response);
            receive.socket.write(packed);
        }).catch(() => {});
    }
}

function checkCode(data, password, receivedCode) {
    const expected = crypto.createHash('md5').update(data).update(password).digest();
    return Buffer.compare(expected, receivedCode) === 0;
}

function pack(obj) {
    const { code, data = Buffer.alloc(0) } = obj;
    const codeBuffer = Buffer.alloc(4);
    codeBuffer.writeUInt32BE(code, 0);
    const payload = Buffer.concat([codeBuffer, data]);
    const hash = crypto.createHash('md5').update(payload).digest();
    const length = Buffer.alloc(4);
    length.writeUInt32BE(payload.length + 16, 0);
    return Buffer.concat([length, hash, payload]);
}

