const net = require('net');
const { EventEmitter } = require('events');

class TcpReader extends EventEmitter {
  constructor(opts, transporter, logger) {
    super();
    this.opts = { ...opts };
    if (this.opts.maxPacketSize === undefined) {
      this.opts.maxPacketSize = 65535;
    }
    this.transporter = transporter;
    this.Promise = transporter.Promise;
    this.logger = logger;
    this.sockets = [];
    this.server = net.createServer((socket) => {
      this.onTcpClientConnected(socket);
    });

    this.server.on('error', (err) => {
      this.logger.error('TCP server error:', err.message);
    });
  }

  listen() {
    return new this.Promise((resolve, reject) => {
      this.server.listen(this.opts.port, () => {
        resolve(this.server.address().port);
      });

      this.server.once('error', (err) => {
        reject(err);
      });
    });
  }

  onTcpClientConnected(socket) {
    this.sockets.push(socket);

    socket.on('data', (data) => {
      if (data.length > this.opts.maxPacketSize) {
        this.logger.error('Packet size exceeds max allowed size');
        this.closeSocket(socket);
        return;
      }
      try {
        this.transporter.parse(data);
      } catch (err) {
        this.logger.error('Error parsing data:', err.message);
      }
    });

    socket.on('error', (err) => {
      this.logger.error('Socket error:', err.message);
      this.closeSocket(socket);
    });

    socket.on('close', () => {
      this.closeSocket(socket);
    });
  }

  closeSocket(socket) {
    try {
      if (!socket.destroyed) {
        socket.destroy();
      }
    } catch (err) {
      // ignore
    }
    const index = this.sockets.indexOf(socket);
    if (index !== -1) {
      this.sockets.splice(index, 1);
    }
  }

  close() {
    this.sockets.forEach((sock) => {
      this.closeSocket(sock);
    });
    this.sockets = [];
    this.server.close();
  }
}

