const EventEmitter = require('events');

class DNodeServer {
    constructor(cons, opts) {
        this.cons = cons;
        this.opts = opts || {};
        this.server = net.createServer(this._handleConnection.bind(this));
        this.server.sessions = {};
    }

    _handleConnection(stream) {
        const id = this._randomId();
        let session;
        if (this.opts.proto) {
            session = Object.create(this.opts.proto);
        } else {
            session = new EventEmitter();
        }
        session.id = id;
        session.stream = stream;
        this.server.sessions[id] = session;

        const ref = this.opts.weak ? new WeakRef(session) : { deref: () => session };
        const wrap = (handler) => {
            return (...args) => {
                const s = ref.deref();
                if (s) handler.call(s, ...args);
            };
        };

        const onData = wrap(function(data) { this.emit('data', data); });
        const onEnd = wrap(function() { this.emit('end'); });
        const onError = wrap(function(err) { this.emit('error', err); });
        const onClose = wrap(function() { this.emit('close'); });

        stream.on('data', onData);
        stream.on('end', onEnd);
        stream.on('error', onError);
        stream.on('close', onClose);

        stream.pipe(session);
        session.pipe(stream);

        this.cons(session, stream);

        const cleanup = () => {
            delete this.server.sessions[id];
            stream.off('data', onData);
            stream.off('end', onEnd);
            stream.off('error', onError);
            stream.off('close', onClose);
        };
        stream.once('end', cleanup);
        stream.once('error', cleanup);
        stream.once('close', cleanup);
    }

    listen(port, host, callback) {
        return this.server.listen(port, host, callback);
    }

    _randomId() {
        return crypto.randomBytes(8).toString('hex');
    }
}

