    this.targetPort = targetPort || 6379;
    this.intermediateData = intermediateData || [];
    this.listenSocket = null;
    this.mode = undefined;
    if (this.intermediateData.length > 0) {
      this.mode = MODES.test;
    } else if (Pythagora) {
      this.mode = MODES.capture;
    }
  }
  init() {
    return new Promise((resolve, reject) => {
      this.listenSocket = net.createServer();
      this.listenSocket.on('connection', clientSocket => {
        this.handleClient(clientSocket);
      });
      this.listenSocket.on('error', reject);
      this.listenSocket.listen(this.listenPort, () => {
        resolve();
      });
    });
  }
  handleClient(clientSocket) {
    const redisSocket = net.createConnection(this.targetPort);
    let currentRequest = null;
    clientSocket.on('data', data => {
      if (this.mode === MODES.test) {
        const req = data.toString();
        const match = this.intermediateData.find(pair => pair.request === req);
