    redisSocket.on('error', errorHandler);
    clientSocket.on('end', () => redisSocket.end());
    redisSocket.on('end', () => clientSocket.end());
  }
  splitIntoChunks(str, chunkSize) {
    const chunks = [];
    for (let i = 0; i < str.length; i += chunkSize) {
