const net = require('net');
const EventEmitter = require('events');

class Node extends EventEmitter {
    constructor(id) {
        super();
        this.id_ = id;
        this.peers_ = {};
        this.PORT = 3000;
        this.server_ = net.createServer((socket) => {
            socket.once('data', (data) => {
                let clientId;
                try {
                    const parsed = JSON.parse(data.toString());
                    clientId = parsed.id;
                } catch (e) {
                    socket.destroy();
                    return;
                }
                this.add_peer(socket, clientId);
            });
        });

        this.server_.on('error', (err) => {
            this.emit('error', err);
        });

        this.server_.listen(this.PORT + id);
    }

    start() {
        return new Promise((resolve, reject) => {
            const maxPeers = 5;
            const connected = [];
            const self = this;

            const possiblePeers = Array.from({ length: 10 }, (_, i) => i).filter(id => id !== this.id_);

            for (let i = possiblePeers.length - 1; i > 0; i--) {
                const j = Math.floor(Math.random() * (i + 1));
                [possiblePeers[i], possiblePeers[j]] = [possiblePeers[j], possiblePeers[i]];
            }

            const candidates = possiblePeers.slice(0, maxPeers);
            let completed = 0;

            candidates.forEach(targetId => {
                const socket = new net.Socket();

                socket.connect(this.PORT + targetId, '127.0.0.1', () => {
                    socket.write(JSON.stringify({ id: self.id_ }) + '\n');
                    self.add_peer(socket, targetId);
                    connected.push(targetId);
                    completed++;
                    if (connected.length >= maxPeers) {
                        resolve(connected);
                    }
                });

                socket.on('error', (err) => {
                    completed++;
                    if (completed === candidates.length) {
                        resolve(connected);
                    }
                });
            });

            if (candidates.length === 0) {
                resolve([]);
            }
        });
    }

    on_data(data, socket) {
        socket._buffer = socket._buffer || '';
        socket._buffer += data.toString();
        const lines = socket._buffer.split('\n');
        socket._buffer = lines.pop();

        lines.forEach(line => {
            if (line.trim() === '') return;
            try {
                const message = JSON.parse(line);
                this.emit('message', message);
            } catch (e) {
                this.emit('error', e);
            }
        });
    }

    send(socketOrId, data) {
        let socket;
        if (typeof socketOrId === 'number') {
            socket = this.peers_[socketOrId];
            if (!socket) {
                throw new Error(`Peer ${socketOrId} not found`);
            }
        } else {
            socket = socketOrId;
        }
        socket.write(JSON.stringify(data) + '\n');
    }

    broadcast(data) {
        Object.keys(this.peers_).forEach(peerId => {
            this.send(parseInt(peerId, 10), data);
        });
    }

    add_peer(socket, remote_id) {
        this.peers_[remote_id] = socket;
        socket.remoteId = remote_id;
        socket._buffer = '';
        socket.on('data', (data) => {
            this.on_data(data, socket);
        });
        socket.on('end', () => {
            this.remove_peer(socket);
        });
        socket.on('close', () => {
            this.remove_peer(socket);
        });
    }

    remove_peer(socket) {
        const remoteId = socket.remoteId;
        if (remoteId !== undefined && this.peers_[remoteId] === socket) {
            delete this.peers_[remoteId];
        }
        socket.destroy();
    }

    list_peers() {
        return Object.keys(this.peers_).map(id => parseInt(id, 10));
    }
}

module.exports = Node;

