const dgram = require('dgram');
const EventEmitter = require('events');

class MasterIPC extends EventEmitter {
  constructor(options) {
    super();
    this.options = Object.assign({}, { port: 0, address: '127.0.0.1' }, options);
    this._sock = null;
    this.sock = null;
    this.children = [];
  }

  IPCSocket() {
    if (!this._sock) {
      this._sock = dgram.createSocket('udp4');
      this._sock.on('message', (msg, rinfo) => this.onMessage(msg, rinfo));
      this.sock = this._sock;
    }
    return this._sock;
  }

  onMessage(msg, rinfo) {
    try {
      const data = JSON.parse(msg.toString());
      if (data.type === 'connect') {
        this.connect(data.id, rinfo);
      } else if (data.type === 'call') {
        this.invoke(data.method, data.args, data.workerId);
      }
    } catch (e) {}
  }

  setupIPC() {
    const socket = this.IPCSocket();
    socket.bind(this.options.port, this.options.address, () => {
      this.emit('start');
    });
  }

  call(id, method, ...args) {
    const worker = this.children.find(w => w.id === id);
    if (worker) {
      const message = JSON.stringify({
        type: 'call',
        method,
        args,
        workerId: id
      });
      this.sock.send(message, worker.port, worker.address);
    }
  }

  invoke(method, args, worker) {
    if (this[method]) {
      this[method].apply(worker, args);
    }
  }

  connect(id, rinfo) {
    this.children.push({ id, address: rinfo.address, port: rinfo.port });
    this.emit('worker connected', { id, address: rinfo.address, port: rinfo.port });
  }

  connectMaster(sig) {
    this.IPCSocket();
    if (!this.sock.address()) {
      this.sock.once('listening', () => {
        process.kill(process.ppid, sig);
      });
    } else {
      process.kill(process.ppid, sig);
    }
  }

  masterKilled(data) {
    this.emit('restart', data);
  }
}

module.exports = MasterIPC;

