const EventEmitter = require('events');
const WebSocket = require('ws');

class Chrome extends EventEmitter {
  constructor(options = {}, notifier) {
    super();
    this.host = options.host || 'localhost';
    this.port = options.port || 9222;
    this.secure = options.secure || false;
    this.webSocketUrl = options.webSocketUrl || `${this.secure ? 'wss' : 'ws'}://${this.host}:${this.port}`;
    this._ws = null;
    this._notifier = notifier;
    this._callbacks = {};
    this._nextCommandId = 1;
    this._queue = [];
  }

  _start() {
    return this._connectToWebSocket();
  }

  _connectToWebSocket() {
    return new Promise((resolve, reject) => {
      try {
        this._ws = new WebSocket(this.webSocketUrl);
        this._ws.onopen = () => {
          resolve();
          this._flushQueue();
        };
        this._ws.onmessage = this._handleMessage.bind(this);
        this._ws.onerror = (error) => {
          this.emit('error', error);
          reject(error);
        };
        this._ws.onclose = this._handleConnectionClose.bind(this);
      } catch (error) {
        reject(error);
      }
    });
  }

  _handleConnectionClose() {
    this._ws = null;
    this._notifier.emit('close');
    Object.values(this._callbacks).forEach(cb => cb.reject(new Error('Connection closed')));
    this._callbacks = {};
  }

  _handleMessage(message) {
    try {
      const data = JSON.parse(message.data);
      if (data.id && this._callbacks[data.id]) {
        const { resolve, reject } = this._callbacks[data.id];
        if (data.error) {
          reject(data.error);
        } else {
          resolve(data.result);
        }
        delete this._callbacks[data.id];
      } else {
        this._notifier.emit(data.method, data.params);
      }
    } catch (error) {
      this.emit('error', error);
    }
  }

  send(method, params, sessionId, callback) {
    const id = this._nextCommandId++;
    const command = { id, method, params };
    if (sessionId) command.sessionId = sessionId;

    if (!this._ws || this._ws.readyState !== WebSocket.OPEN) {
      this._enqueueCommand(command, callback);
      return callback ? undefined : Promise.resolve();
    }

    if (callback) {
      this._callbacks[id] = { resolve: callback, reject: callback };
    } else {
      this._callbacks[id] = {};
      this._callbacks[id].promise = new Promise((resolve, reject) => {
        this._callbacks[id].resolve = resolve;
        this._callbacks[id].reject = reject;
      });
    }

    this._ws.send(JSON.stringify(command));
    return this._callbacks[id].promise;
  }

  _enqueueCommand(command, callback) {
    this._queue.push({ command, callback });
  }

  _flushQueue() {
    this._queue.forEach(({ command, callback }) => {
      const id = command.id;
      if (callback) {
        this._callbacks[id] = { resolve: callback, reject: callback };
      } else {
        this._callbacks[id] = {};
        this._callbacks[id].promise = new Promise((resolve, reject) => {
          this._callbacks[id].resolve = resolve;
          this._callbacks[id].reject = reject;
        });
      }
      this._ws.send(JSON.stringify(command));
    });
    this._queue = [];
  }

  close(callback) {
    return new Promise((resolve, reject) => {
      if (!this._ws) {
        const err = new Error('Not connected');
        if (callback) callback(err);
        reject(err);
        return;
      }
      this._ws.onclose = () => {
        this._handleConnectionClose();
        if (callback) callback();
        resolve();
      };
      this._ws.close();
    });
  }
}

