const WebSocket = require('ws');

class CustomEnvironment {
  constructor(url) {
    this.url = url;
    this.callbacks = new Map();
    this.idSeq = 0;
    this.ws = null;
    this.isConnecting = false;
    this.reconnectAttempts = 0;
    this.maxReconnectAttempts = 5;
    this.reconnectDelay = 1000;
  }

  initWs() {
    return new Promise((resolve, reject) => {
      if (this.ws && this.ws.readyState === WebSocket.OPEN) {
        resolve();
        return;
      }

      if (this.isConnecting) return;
      this.isConnecting = true;

      const connect = () => {
        const ws = new WebSocket(this.url);
        this.ws = ws;

        ws.on('open', () => {
          this.isConnecting = false;
          this.reconnectAttempts = 0;
          this.idSeq = 0;
          resolve();
        });

        ws.on('message', (data) => {
          try {
            const message = JSON.parse(data);
            const { id, data: response } = message;
            if (this.callbacks.has(id)) {
              const { resolve, reject, timeout } = this.callbacks.get(id);
              clearTimeout(timeout);
              this.callbacks.delete(id);
              if (message.error) {
                reject(new Error(message.error));
              } else {
                resolve(response);
              }
            }
          } catch (err) {
            console.error('Message parsing error:', err);
          }
        });

        ws.on('error', (err) => {
          console.error('WebSocket error:', err);
          this.handleReconnection(err);
          if (this.reconnectAttempts === 0) reject(err);
        });

        ws.on('close', (code, reason) => {
          if (code !== 1000) {
            this.handleReconnection(new Error(`Closed: ${code} ${reason}`));
          }
        });
      };

      connect();
    });
  }

  handleReconnection(error) {
    for (const [id, { resolve, reject, timeout }] of this.callbacks.entries()) {
      clearTimeout(timeout);
      reject(error);
    }
    this.callbacks.clear();

    if (this.reconnectAttempts < this.maxReconnectAttempts) {
      setTimeout(() => {
        this.reconnectAttempts++;
        this.initWs().catch(err => {
          console.error(`Reconnect attempt ${this.reconnectAttempts} failed:`, err);
        });
      }, this.reconnectDelay * Math.pow(2, this.reconnectAttempts));
    }
  }

  runCommand(type, data) {
    return new Promise((resolve, reject) => {
      if (!this.ws || this.ws.readyState !== WebSocket.OPEN) {
        reject(new Error('WebSocket not connected'));
        return;
      }

      const id = ++this.idSeq;
      const timeoutDuration = 5000;
      const timeout = setTimeout(() => {
        this.callbacks.delete(id);
        reject(new Error('Command timeout'));
      }, timeoutDuration);

      this.callbacks.set(id, { resolve, reject, timeout });

      try {
        this.ws.send(JSON.stringify({ type, data, id }));
      } catch (err) {
        clearTimeout(timeout);
        this.callbacks.delete(id);
        reject(err);
      }
    });
  }

  teardown() {
    if (this.ws) {
      this.ws.removeAllListeners();
      this.ws.close(1000);
      this.ws = null;
    }

    for (const [id, { resolve, reject, timeout }] of this.callbacks.entries()) {
      clearTimeout(timeout);
      reject(new Error('Connection closed'));
    }
    this.callbacks.clear();
  }
}

