class WebSocketClient {
  constructor(subscriptionKey) {
    this.subscriptionKey = subscriptionKey;
    this.ws = null;
    this.executorMap = new Map();
    this.bufferMap = new Map();
    this.timer = null;
  }

  connect() {
    return new Promise((resolve, reject) => {
      const ws = new WebSocket('wss://westus.tts.speech.microsoft.com/cognitiveservices/websocket/v1', {
        headers: {
          'Ocp-Apim-Subscription-Key': this.subscriptionKey
        }
      });

      const timeout = setTimeout(() => {
        reject(new Error('Connection timeout'));
        ws.terminate();
      }, 10000);

      ws.once('open', () => {
        clearTimeout(timeout);
        this.ws = ws;
        resolve(ws);
      });

      ws.once('error', (error) => {
        clearTimeout(timeout);
        reject(error);
      });

      ws.on('message', (data) => {
        if (typeof data === 'string') {
          try {
            const message = JSON.parse(data);
            if (message['turn.end']) {
              const requestId = message['turn.end'].requestid;
              const entry = this.executorMap.get(requestId);
              if (entry) {
                const buffer = this.bufferMap.get(requestId);
                entry.resolve(buffer);
                this.executorMap.delete(requestId);
                this.bufferMap.delete(requestId);
              }
            }
          } catch (err) {
            // Ignore parsing errors
          }
        } else {
          // Binary data handling (simplified, assumes single active request)
          for (const [requestId, buffer] of this.bufferMap.entries()) {
            this.bufferMap.set(requestId, Buffer.concat([buffer, data]));
            break;
          }
        }
      });
    });
  }

  sendRequest(ssml, format) {
    const requestId = uuidv4();
    let resolvePromise, rejectPromise;
    const promise = new Promise((resolve, reject) => {
      resolvePromise = resolve;
      rejectPromise = reject;
    });
    this.executorMap.set(requestId, { resolve: resolvePromise, reject: rejectPromise });
    this.bufferMap.set(requestId, Buffer.alloc(0));

    // Send speech configuration
    this.ws.send(JSON.stringify({
      context: {
        system: {
          name: 'SpeechSDK',
          version: '1.0.0'
        }
      },
      synthesis: {
        audio: {
          outputFormat: format
        }
      }
    }));

    // Send turn start
    this.ws.send(JSON.stringify({
      'turn.start': {
        requestid: requestId
      }
    }));

    // Send SSML synthesis request
    this.ws.send(JSON.stringify({
      synthesize: {
        ssml: ssml
      }
    }));

    return promise;
  }
}

