const EventEmitter = require('events');
const WebSocket = require('ws');
const fetch = require('node-fetch');

class OrderBook extends EventEmitter {
  static STATES = {
    INITIAL: 'initial',
    CONNECTING: 'connecting',
    SYNCING: 'syncing',
    ACTIVE: 'active',
    CLOSED: 'closed',
    ERROR: 'error'
  };

  constructor(productID, websocketURI, restURI, timeProvider) {
    super();
    this.productID = productID;
    this.websocketURI = websocketURI;
    this.restURI = restURI;
    this.timeProvider = timeProvider || Date;
    this.state = OrderBook.STATES.INITIAL;
    this.fail_count = 0;
    this.socket = null;
    this.queue = [];
    this.book = {
      sequence: 0,
      bids: [],
      asks: []
    };
    this.connect();
  }

  clear_book() {
    this.queue = [];
    this.book = {
      sequence: 0,
      bids: [],
      asks: []
    };
  }

  connect() {
    this.disconnect();
    this.changeState(OrderBook.STATES.CONNECTING);
    this.socket = new WebSocket(this.websocketURI);
    this.socket.onopen = () => this.onOpen();
    this.socket.onclose = () => this.onClose();
    this.socket.onmessage = (event) => this.onMessage(event.data);
    this.socket.onerror = (err) => {
      this.emit('error', err);
      this.disconnect();
    };
  }

  disconnect() {
    if (this.socket) {
      const ws = this.socket;
      this.socket = null;
      ws.removeAllListeners();
      ws.close();
    }
  }

  changeState(stateName) {
    const prevState = this.state;
    this.state = stateName;
    this.emit('statechange', { previous: prevState, current: stateName });
  }

  onOpen() {
    this.changeState(OrderBook.STATES.SYNCING);
    this.sync();
  }

  onClose() {
    this.changeState(OrderBook.STATES.CLOSED);
    this.clear_book();
  }

  async onMessage(datastr) {
    let message;
    try {
      message = JSON.parse(datastr);
    } catch (err) {
      this.emit('error', new Error('Invalid message format'));
      return;
    }
    if (this.state !== OrderBook.STATES.ACTIVE) {
      this.queue.push(message);
    } else {
      this.processMessage(message, this.timeProvider.now ? this.timeProvider.now() : new Date());
    }
  }

  async sync() {
    const subscribeMsg = {
      type: 'subscribe',
      product_ids: [this.productID],
      channels: ['level2']
    };
    this.socket.send(JSON.stringify(subscribeMsg));

    try {
      const response = await fetch(this.restURI);
      if (!response.ok) throw new Error(`REST fetch failed: ${response.statusText}`);
      const snapshot = await response.json();
      this.book.sequence = snapshot.sequence;
      this.book.bids = snapshot.bids.map(([price, size]) => [parseFloat(price), parseFloat(size)]);
      this.book.asks = snapshot.asks.map(([price, size]) => [parseFloat(price), parseFloat(size)]);
      this.book.bids.sort((a, b) => b[0] - a[0]);
      this.book.asks.sort((a, b) => a[0] - b[0]);
      const queue = [...this.queue];
      this.queue = [];
      for (const msg of queue) {
        this.processMessage(msg, this.timeProvider.now ? this.timeProvider.now() : new Date());
      }
      this.changeState(OrderBook.STATES.ACTIVE);
    } catch (err) {
      this.emit('error', err);
      this.disconnect();
      this.changeState(OrderBook.STATES.ERROR);
    }
  }

  processMessage(message, t) {
    if (message.type === 'l2update') {
      const expectedSeq = this.book.sequence + 1;
      if (message.sequence !== expectedSeq) {
        this.emit('error', new Error('Out of order sequence'));
        this.disconnect();
        return;
      }
      this.book.sequence = message.sequence;

      for (const [priceStr, sizeStr] of message.updates) {
        const price = parseFloat(priceStr);
        const size = parseFloat(sizeStr);
        if (size === 0) {
          if (price < 0) {
            this.book.bids = this.book.bids.filter(bid => bid[0] !== price);
          } else {
            this.book.asks = this.book.asks.filter(ask => ask[0] !== price);
          }
        } else {
          const side = price < 0 ? this.book.bids : this.book.asks;
          const existing = side.find(order => order[0] === price);
          if (existing) {
            existing[1] = size;
          } else {
            side.push([price, size]);
          }
          if (price < 0) {
            this.book.bids.sort((a, b) => b[0] - a[0]);
          } else {
            this.book.asks.sort((a, b) => a[0] - b[0]);
          }
        }
      }

      this.emit('update', {
        timestamp: t,
        product_id: this.productID,
        sequence: message.sequence,
        bids: this.book.bids,
        asks: this.book.asks
      });
    } else {
      this.emit(message.type, message);
    }
  }
}

