  function processQueue() {
    while (numRunning < maxParallelRuns && queue.length > 0) {
      const testUrl = queue.shift();
      const fullUrl = testUrl + (testUrl.includes('?') ? '&' : '?') + `session=${subscriptionKey}`;
      window.open(fullUrl, '_blank');
      numRunning++;
    }
  }

  socket.onopen = function() {
    socket.send(JSON.stringify({ type: 'subscribe', key: subscriptionKey }));
    processQueue();
  };

  socket.onmessage = function(event) {
    const message = JSON.parse(event.data);
    if (message.key === subscriptionKey) {
      const statusEl = document.getElementById(`test-status-${message.id}`);
      if (statusEl) statusEl.textContent = message.status;
      if (message.completed) {
        numRunning--;
        processQueue();
      }
    }
  };
}

