    }  
    const data = this._formatData(method, params, id);  
    this._handles[id] = { success: successCallback, error: errorCallback };  
    this._send(data);  
  }  

  batchRequest(requests, successCallback, errorCallback) {  
    const batch = requests.map(req => {  
      const id = req.id || this._generateId();  
      if (!req.id) req.id = id;  
      this._handles[id] = { success: successCallback, error: errorCallback };  
      return this._formatData(req.method, req.params, id);  
    });  
    this._send(batch);  
  }  

  _formatData(method, params, id) {  
    const jsonRpc = {  
      jsonrpc: '2.0',  
      method: this._namespace + method,  
      params: params  
    };  
    if (id !== undefined) {  
      jsonRpc.id = id;  
    }  
    return jsonRpc;  
  }  

  _send(data) {  
    if (this._socket && this._socket.readyState === WebSocket.OPEN) {  
      this._socket.send(JSON.stringify(data));  
    } else {  
      this._queue.push(data);  
    }  
  }  

  _connect() {  
    this._socket = new WebSocket(this._url);  
    this._setupSocketHandlers();  
  }  

  _setupSocketHandlers() {  
    this._socket.onopen = () => {  
      while (this._queue.length > 0) {  
        const data = this._queue.shift();  
        this._send(data);  
      }  
    };  

    this._socket.onmessage = (event) => {  
      this._onmessage(event.data, this._handles, this._listeners);  
    };  

    this._socket.onerror = (error) => {  
      this._onerror(error, this._handles);  
      this._socket.close();  
    };  

    this._socket.onclose = () => {  
      setTimeout(() => this._connect(), 5000);  
    };  
  }  

  _generateId() {  
    return Math.random().toString(36).substr(2, 9);  
  }  

  _onmessage(message, handles, listeners) {  
    const data = JSON.parse(message);  
    if (Array.isArray(data)) {  
      data.forEach(response => this._processResponse(response, handles, listeners));  
    } else {  
      this._processResponse(data, handles, listeners);  
    }  
  }  

  _processResponse(response, handles, listeners) {  
    if (response.id) {  
      const handle = handles[response.id];  
      if (handle) {  
        if (response.error) {  
          handle.error(response.error);  
        } else if (response.result) {  
          handle.success(response.result);  
        }  
        delete handles[response.id];  
      }  
    } else if (response.method) {  
      const listener = listeners[response.method];  
      if (listener) {  
        listener(response.params);  
      }  
    }  
  }  

  _onerror(error, handles) {  
    Object.values(handles).forEach(handle => {  
      if (handle.error) {  
        handle.error(error);  
      }  
    });  
    this._handles = {};  
  }  
}

